import React, { Fragment, Component } from 'react';
import Nav from '../component/nav/nav'
import TableComponent from '../component/table/table'
import TopicSearch from '../component/topic_search/topic_search'

import { getAllPostsByText } from '../lib/api'


class Index extends Component {

    state = {
        message: 'props is work'
    };

    render(){
        const { index, posts, query } = this.props;
        const { topics, dist, before, after } = posts;
        const { text } = query;
        console.log('Search component : ' + text);

        return(
            <div>
                <Nav/>
                <TopicSearch text={text}/>
                <TableComponent list={topics} />
            </div>
        )
    }
}

export default Index;


export async function getServerSideProps(context) {

    const { query} = context;
    const { limit = 10, after = '', before = '', text = '' } = query;

    const result = (await getAllPostsByText(limit, after, before, text)) || [];


    const posts = result.search || [];


    return {
        props: {
            posts,
            query,
        },
    }
}
