    const { limit, after, before } = query;
    const { index } = params;

    const result = (await getAllPosts(index, limit, after, before)) || [];

    console.log('index: ' + index);
    console.log('list size: ' + result.hot.length);


    const list = result[index];


    return {
        props: {
            list,
            query,
            index,
        },
    }
}
