import React, { Fragment, Component } from 'react';
import Link from 'next/link';
import Message from './message'
import {hotTopicsQueries} from "../component/hot/queries";
import { gql } from 'apollo-boost';
import { graphql } from 'react-apollo';
import { useQuery } from '@apollo/react-hooks';
import { useRouter } from 'next/router'

import { getAllPosts } from '../lib/api'


class Index extends Component {
    state = {
        message: 'props is work'
    };



    render(){
        const router = useRouter()
        const { people } = this.props;
        console.log('props: ' + router.query);

        return(
            <div>
                <Link href={'/'}>
                    <a>Home</a>
                </Link>
                <Link as={'/lists'} href={'/lists'} children={Message}>
                    <a>Lists</a>
                </Link>
                <Link as={'/food'} href={'/food'}>
                    <a>Food</a>


                </Link>
                <Link as={'/food/bob'} href={'/[file]/[action]'} >
                    <a>List</a>
                </Link>

                {  }

                <div>Hello World!</div>
            </div>
        )
    }
}

export default Index;


export async function getServerSideProps(context) {

    console.log('server: param: ' + context.params.index);
    console.log('server: query: ' + context.query.after);

    const { name, limit, after, before } = context.query;


    const result = (await getAllPosts(name,limit, after, before)) || [];

    console.log('Graphql: ' + result);

    // const allPosts = (await getAllPostsForHome(preview)) || []
    // const allPosts = (await getAllPostsForHome(preview)) || []



    return {
        props: {
            result
        },
    }

}


