const BASE_API = 'http://localhost:3005/api';
const axios = require('axios');


async function fetchAPI(query, { variables } = {}) {

    const res = await axios.post(BASE_API, JSON.stringify({query, variables,}).replace(/\\n/g, ''), {
        headers: {
            'Content-Type': 'application/json',
        }
    })
        .then(res => {

            return res.data.data;
        })
        .catch(err => {
            console.log(err);
            return [];
        });

    return res;
}


export async function getAllPostsWithSlug(limit, ) {
    const data = await fetchAPI(`
    query($limit: Int, $after: String, $before: String){
       hot(limit: $limit, after: $after, Before: $before){
            topics{
              subreddit
              id
              title
              score
              thumbnail
              url
            }
           before
           after
           dist
        }
    }
  `);

  return data;
}
