import React, {Component, Fragment} from 'react';
import TextField from '@material-ui/core/TextField';
import TableComponent from "../table/table";
import Select from '@material-ui/core/Select';
import Grid from '@material-ui/core/Grid';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import PropTypes from "prop-types";
import { InputBase } from '@material-ui/core';

class Search extends Component {

    state = {};

    static propTypes = {
        limit: PropTypes.number,
        changeLimit: PropTypes.func,
        changeAfterAndBefore: PropTypes.func,
        changeSearchText: PropTypes.func,
        after: PropTypes.string,
        before: PropTypes.string,
        searchText: PropTypes.string
    };

    static defaultProps = {
        limit: 100,
        changeLimit: (vl) => { alert(vl) },
        changeAfterAndBefore: (after, before) => { alert('after: ' + after + ', before: ' + before) },
        changeSearchText: ( text ) => { alert('text: ' + text) },
        after: '',
        before: '',
        searchText: ''
    };

    componentDidMount(): void {}
    componentWillUnmount(): void {}

    limitChange = (event: React.ChangeEvent<{ value: unknown }>) => {
        this.props.changeLimit(event.target.value);
    };

    searchTextChange = (event: React.ChangeEvent<{ value: unknown }>) => {
        this.props.changeSearchText(event.target.value);
    };

    render() {
        const { data = {}, limit, searchText } = this.props;
        const { search = [] } = data;
        return (
            <Fragment>
                <Grid container spacing={3} >
                    <Grid item xs={12} md={1}>
                        <FormControl variant="outlined" margin={"normal"}>
                            <InputLabel htmlFor="outlined-age-native-simple">Limit</InputLabel>
                            <Select
                                native
                                value={limit}
                                onChange={this.limitChange}
                                label="Age"
                                inputProps={{
                                    name: 'age',
                                    id: 'outlined-age-native-simple',
                                }}
                            >
                                <option aria-label="None" value=""/>
                                <option value={10}>10</option>
                                <option value={25}>25</option>
                                <option value={50}>50</option>
                                <option value={100}>100</option>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12} md={11}>
                        <TextField
                            value={ searchText }
                            onChange={this.searchTextChange}
                            fullWidth={true}
                            id="outlined-number"
                            label="Search"
                            type="text"
                            margin={"normal"}
                            InputLabelProps={{
                                shrink: true,
                            }}
                            variant="outlined"
                        />
                    </Grid>
                </Grid>
                { searchText }
                <TableComponent list={search}/>
            </Fragment>
        );
    }

}

export default Search;
