import React, { Component } from 'react'



class Props extends Component {

    state = {
        counter: 0,
    };

    handleClick = () => {
        this.setState(({ counter }) => ({
            counter: ++counter,
        }))
    }

    render = () => {
        const { counter } = this.state;
        return (
            <div>
                <div>{ counter }</div>
                <button OnClick=""></button>
            </div>
        )
    }

}

export default Props;
