import React, {Component, Fragment} from 'react'
import withHoc from './topicHoc';
import PropTypes from "prop-types";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel/InputLabel";
import Select from "@material-ui/core/Select/Select";
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Grid from '@material-ui/core/Grid';


class Topic extends Component {

    state = {
        expanded: false
    };

    static propTypes = {
        commentLimit: PropTypes.number,
        repliesLimit: PropTypes.number,
        permalink: PropTypes.string,
        changeCommentLimit: PropTypes.func,
        changeRepliesLimit: PropTypes.func,
        changePermaLink: PropTypes.func,
    };

    static defaultProps = {
        commentLimit: 100,
        repliesLimit: 100,
        permalink: '',
        changeCommentLimit: (vl) => { alert(vl) },
        changeRepliesLimit: (vl) => { alert(vl) },
        changePermaLink: (vl) => { alert(vl) },

    };

    componentDidMount(): void {}
    componentWillUnmount(): void {}

    changeCommentLimit = (event: React.ChangeEvent<{ value: unknown }>) => {
        this.props.changeCommentLimit(event.target.value);
    };

    changeRepliesLimit = (event: React.ChangeEvent<{ value: unknown }>) => {
        this.props.changeRepliesLimit(event.target.value);
    };

    permalinkChange = ( link ) => {
        this.props.changePermaLink(link);
    };

    handleExpandClick = () => {
        this.setState(({ expanded }) => ({ expanded: !expanded }))
    };

    render = () => {
        const { expanded } = this.state;
        const { topicAndComment = {}, commentLimit, repliesLimit } = this.props;

        return (
            <Fragment>
                <Grid container spacing={3}>
                    <Grid item xs={12} md={12}>
                        {}
                    </Grid>
                </Grid>

                <Card>
                    <CardHeader
                        avatar={
                            <Avatar aria-label="recipe">
                                R
                            </Avatar>
                        }
                        action={
                            <IconButton aria-label="settings">
                                <MoreVertIcon />
                            </IconButton>
                        }
                        title="Shrimp and Chorizo Paella"
                        subheader="September 14, 2016"
                    />
                    <CardMedia
                        image="/static/images/cards/paella.jpg"
                        title="Paella dish"
                    />
                    <CardContent>
                        <Typography variant="body2" color="textSecondary" component="p">
                            This impressive paella is a perfect party dish and a fun meal to cook together with your
                            guests. Add 1 cup of frozen peas along with the mussels, if you like.
                        </Typography>
                    </CardContent>
                    <CardActions disableSpacing>
                        <IconButton aria-label="add to favorites">
                            <FavoriteIcon />
                        </IconButton>
                        <IconButton aria-label="share">
                            <ShareIcon />
                        </IconButton>
                        <IconButton
                            onClick={this.handleExpandClick}
                            aria-expanded={expanded}
                            aria-label="show more"
                        >
                            <ExpandMoreIcon />
                        </IconButton>
                    </CardActions>
                    <Collapse in={expanded} timeout="auto" unmountOnExit>
                        <CardContent>
                            <Typography paragraph>Method:</Typography>
                            <Typography paragraph>
                                Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
                                minutes.
                            </Typography>
                            <Typography paragraph>
                                Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
                                heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
                                browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving chicken
                                and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion, salt and
                                pepper, and cook, stirring often until thickened and fragrant, about 10 minutes. Add
                                saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
                            </Typography>
                            <Typography paragraph>
                                Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
                                without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat to
                                medium-low, add reserved shrimp and mussels, tucking them down into the rice, and cook
                                again without stirring, until mussels have opened and rice is just tender, 5 to 7
                                minutes more. (Discard any mussels that don’t open.)
                            </Typography>
                            <Typography>
                                Set aside off of the heat to let rest for 10 minutes, and then serve.
                            </Typography>
                        </CardContent>
                    </Collapse>
                </Card>


                <FormControl variant="outlined" margin={"normal"}>
                    <InputLabel htmlFor="outlined-age-native-simple">Comment limit</InputLabel>
                    <Select
                        native
                        value={commentLimit}
                        onChange={this.changeCommentLimit}
                        label="Age"
                        inputProps={{
                            name: 'age',
                            id: 'outlined-age-native-simple',
                        }}
                    >
                        <option aria-label="None" value=""/>
                        <option value={10}>10</option>
                        <option value={25}>25</option>
                        <option value={50}>50</option>
                        <option value={100}>100</option>
                    </Select>
                </FormControl>
            </Fragment>
        )
    }

}


export default withHoc(Topic);
