import React, { Component, Fragment } from 'react';
import TableComponent from '../table/table';
import withHocs from './hotHoc';
import PropTypes from "prop-types";
import InputLabel from "@material-ui/core/InputLabel/InputLabel";
import Select from "@material-ui/core/Select/Select";
import FormControl from "@material-ui/core/FormControl";


class Hot extends Component {

    state = {
        limitPage: 10
    };

    static propTypes = {
        limit: PropTypes.number,
        changeLimit: PropTypes.func,
        after: PropTypes.string,
        before: PropTypes.string
    };

    static defaultProps = {
        limit: 100,
        changeLimit: (vl) => {},
        after: '',
        before: ''
    };

    componentDidMount(): void {

    }

    limitChange = (event: React.ChangeEvent<{ value: unknown }>) => {
        this.setState({changeLimit: (event.target.value) })
    };

    render() {
        const { data = {}, limit } = this.props;
        const { hot = [] } = data;
        const { limitPage } = this.state;

        // const { Hot = {} } = this.props;
        // Hot({ limit: 100, after: '', before:''});

        return (
            <Fragment>
                { limit }
                <FormControl variant="outlined" margin={"normal"}>
                    <InputLabel htmlFor="outlined-age-native-simple">Limit</InputLabel>
                    <Select
                        native
                        value={limitPage}
                        onChange={this.limitChange}
                        label="Age"
                        inputProps={{
                            name: 'age',
                            id: 'outlined-age-native-simple',
                        }}
                    >
                        <option aria-label="None" value=""/>
                        <option value={10}>10</option>
                        <option value={25}>25</option>
                        <option value={50}>50</option>
                        <option value={100}>100</option>
                    </Select>
                </FormControl>
                <TableComponent list={hot}/>
            </Fragment>
        )
    }

}

export default withHocs(Hot);
