import React, { Component, Fragment } from 'react';
import TableComponent from '../table/table';
import withHocs from './hotHoc';
import PropTypes from "prop-types";


class Hot extends Component {

    state = {

    };

    static propTypes = {
        limit: propTypes.number,

    };

    static defaultProps = {

    };

    componentDidMount(): void {

    }

    limitRef = React.createRef();

    limitChange = () => {
        const { limit = 25 } = this.props;

    }

    render() {
        const { data = {} } = this.props;
        const { hot = [] } = data;

        // const { Hot = {} } = this.props;
        // Hot({ limit: 100, after: '', before:''});

        return (
            <Fragment>
                <input type="number" ref={this.limitRef} onChange={this.limitChange}/>
                <TableComponent list={hot}/>
            </Fragment>
        )
    }

}

export default withHocs(Hot);
