import React, {Component, Fragment} from 'react';


import TableComponent from '../table/table'
import BottomNavigationAction from "@material-ui/core/BottomNavigationAction";
import BottomNavigation from "@material-ui/core/BottomNavigation";
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteIcon2 from '@material-ui/icons/AllInbox';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Hot from '../hot/hot'



class Posts extends Component {

    state = {
        count: 20,
        value: 0,
        list: []
    };

    changeItemOfMenu = ( vl ) => {
        this.setState({
            value: vl
        });
        alert(vl);

    };

    a11yProps = (index) => {
        return {
            id: `wrapped-tab-${index}`,
            'aria-controls': `wrapped-tabpanel-${index}`,
        };
    };

    render () {
        const { value } = this.state;
        console.log(this.props.data);
        return (
            <Fragment>
                <div>
                    <AppBar position="static">
                        <Tabs value={value} onChange={this.changeItemOfMenu} aria-label="wrapped label tabs example">
                            <Tab
                                value="one"
                                label="New Arrivals in the Longest Text of Nonfiction"
                                wrapped
                                {...this.a11yProps('one')}
                            />
                            <Tab value="two" label="Item Two" {...this.a11yProps('two')} />
                            <Tab value="three" label="Item Three" {...this.a11yProps('three')} />
                        </Tabs>
                    </AppBar>
                    <TabPanel value={value} index="one">
                        Item One
                    </TabPanel>
                    <TabPanel value={value} index="two">
                        Item Two
                    </TabPanel>
                    <TabPanel value={value} index="three">
                        Item Three
                    </TabPanel>
                </div>
            </Fragment>
        )
    }

}

export default Posts;
