import React, { Component, Fragment } from 'react';
import TableComponent from '../table/table';
import Pagination from '../pagination/pagination';
import withHocs from './hotHoc';
import PropTypes from "prop-types";
import InputLabel from "@material-ui/core/InputLabel/InputLabel";
import Select from "@material-ui/core/Select/Select";
import FormControl from "@material-ui/core/FormControl";
import Grid from '@material-ui/core/Grid';


class Hot extends Component {

    state = {};

    static propTypes = {
        limit: PropTypes.number,
        changeLimit: PropTypes.func,
        changeAfterAndBefore: PropTypes.func,
        changePermaLink: PropTypes.func,
        after: PropTypes.string,
        before: PropTypes.string
    };

    static defaultProps = {
        limit: 100,
        changeLimit: (vl) => { alert(vl) },
        changePermaLink: (vl) => { alert(vl) },
        changeAfterAndBefore: (after, before) => { alert('after: ' + after + ', before: ' + before) },
        after: '',
        before: ''
    };

    componentDidMount(): void {}
    componentWillUnmount(): void {}



    permalinkChange = ( link ) => {
        this.props.changePermaLink(link);
    };

    render() {
        const { data = {}, limit } = this.props;
        const { hot = {} } = data;
        const { topics, after, before } = hot;

        return (
            <Fragment>
                <TableComponent list={topics}/>
                <Pagination before={before} after={after} />
            </Fragment>
        )
    }
}

export default withHocs(Hot);
