import React, {Component, Fragment} from 'react';

import withHocs from './newsHoc';
import TableComponent from '../table/table'
import BottomNavigationAction from "@material-ui/core/BottomNavigationAction";
import BottomNavigation from "@material-ui/core/BottomNavigation";
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteIcon2 from '@material-ui/icons/AllInbox';

class News extends Component {

    state = {
        count: 20,
        value: 0
    };

    changeItemOfMenu = ( vl ) => (
        this.setState({ value: vl })
    );


    render () {
        const { hot = []} = this.props.data;
        const { count, value } = this.state;
        return (
            <Fragment>
                <BottomNavigation
                    value={value}
                    onChange={(event, newValue) => {
                        this.changeItemOfMenu(newValue);
                    }}
                    showLabels
                >
                    <BottomNavigationAction label="Hot" icon={<RestoreIcon/>}/>
                    <BottomNavigationAction label="New" icon={<FavoriteIcon/>}/>
                    <BottomNavigationAction label="Search" icon={<FavoriteIcon2/>}/>
                </BottomNavigation>
                <TableComponent list={ hot } />
            </Fragment>
        )
    }

}

export default withHocs(News);
