import { compose } from 'recompose';
import { graphql } from 'react-apollo';
import { hotTopicsQueries } from './queries';

const withGraphQLData = graphql(hotTopicsQueries, {
    options: ({ Hot }) => ({
        variables: Hot
    })
});

export default compose(withGraphQLData);
