import React, { Component, Fragment } from 'react';
import { ApolloProvider } from 'react-apollo'
import ApolloClient from 'apollo-boost'
import Hot from '../hot/hot'
import New from '../new/new'
import Search from '../search/search'
import Nav from '../nav/nav'
import Topic from '../topic/topic'
import Container from '@material-ui/core/Container';
import { Switch, Route } from "react-router-dom";


const client = new ApolloClient({
    uri: 'http://localhost:3005/api'
});


class App extends Component {

    state = {
        limit: 10,
        after: '',
        before: '',
        searchText: '',
        permalink: '',
    };

    changeLimit = (vl) => {
        this.setState({ limit: Number(vl) })
    };

    changeAfterAndBefore = ( after, before ) => {
        this.setState({ after: after, before: before })
    };

    changeSearchText = ( text ) => {
        this.setState( ({searchText: text}))
    };

    changePermaLink = ( link ) => {
        this.setState( ({permalink: link}))
    };

    render() {
        const { children = null } = this.props;
        const { limit, after, before,searchText } = this.state;
        return (
            <ApolloProvider client={ client }>
                <Fragment>
                    <div className={'container-fluid'}>
                        <Nav/>
                    </div>
                    <Container>
                        <Switch>
                            <Route exact path='/' component={() => <Hot limit={ limit } after={after} before={before} changeLimit={ this.changeLimit }
                                        changeAfterAndBefore={ this.changeAfterAndBefore } changePermaLink={ this.changePermaLink } />}/>
                            <Route path='/new' component={() => <New limit={ limit } after={after} before={before} changeLimit={ this.changeLimit }
                                        changeAfterAndBefore={ this.changeAfterAndBefore } changePermaLink={ this.changePermaLink } />}/>
                            <Route path='/search' component={() => <Search limit={ limit } after={after} before={before} changeLimit={ this.changeLimit }
                                        changeAfterAndBefore={ this.changeAfterAndBefore } searchText={searchText} changeSearchText={this.changeSearchText} changePermaLink={ this.changePermaLink }
                            />}/>
                            <Route path='/topic' component={() => <Topic limit={ limit } after={after} before={before}
                                                                         changeLimit={ this.changeLimit } changeAfterAndBefore={ this.changeAfterAndBefore } changePermaLink={ this.changePermaLink } />}/>
                        </Switch>
                    </Container>
                    { children }
                </Fragment>
            </ApolloProvider>
        )
    }
}

export default App;
