import React, {Component, Fragment} from 'react';


class Form extends Component {


    state = {
        inputText: '',
        textAreaText: '',
        showData: {
            name: '',
            text: '',
        }
    };

    handleInputChange = ({ target: { value } }) => {
        this.setState({
            inputText: value,
        })
    };

    handleTextareaChange = ({ target: { value } }) => {
        this.setState({
            textAreaText: value,
        })
    };

    handleShow = (e) => {
        e.preventDefault();
        const { inputText, textAreaText } = this.state;
        this.setState({
            inputText: '',
            textAreaText: '',
            showData: {
                name: inputText,
                text: textAreaText,
            }
        })
    };


    render(){
        const { inputText, textAreaText, showData } = this.state;
        const { name, text } = this.showData;
        return (
            <Fragment>
                <form>
                    <label>
                        Name:
                        <input type="text" name="name" value={ inputText } onChange={this.handleInputChange} />
                    </label>
                    <label htmlFor="text">Text:</label>
                    <textarea id="text" value={textAreaText} onChange={this.handleTextareaChange}></textarea>
                    <button onClick={this.handleShow}>Show</button>
                    <h2>{ name }</h2>
                    <h3>{ text }</h3>
                </form>
            </Fragment>
        )
    }

}

export default Form;
